import React, { useEffect, useRef, useState } from "react";
import "../styles/Home.css";

const floatingWords = [
  { word: "Front-end", bold: true },
  { word: "Backend", bold: true },
  { word: "UI/UX", bold: true },
  { word: "React" },
  { word: "Next.js" },
  { word: "JavaScript" },
  { word: "HTML" },
  { word: "CSS" },
  { word: "Python" },
  { word: "Design" },
  { word: "MongoDB" },
  { word: "Express" },
  { word: "TypeScript" },
  { word: "Animation" },
  { word: "Portfolio" },
];

function Home() {
  const heroRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => setIsVisible(entry.isIntersecting),
      { threshold: 0.1 }
    );

    if (heroRef.current) {
      observer.observe(heroRef.current);
    }

    return () => {
      if (heroRef.current) {
        observer.unobserve(heroRef.current);
      }
    };
  }, []);

  return (
    <section className={`hero-section ${isVisible ? "visible" : ""}`} ref={heroRef}>
      {/* Floating Words */}
      <div className="floating-words-container">
        {floatingWords.map((item, index) => (
          <span
            key={index}
            className={`floating-word ${item.bold ? "bold" : ""}`}
            style={{
              animationDelay: `${index * 0.3}s`,
              animationDuration: `${6 + Math.random() * 5}s`,
            }}
          >
            {item.word}
          </span>
        ))}
      </div>

      {/* Text Side */}
      <div className="text-side">
        <h1 className="intro-title">Hi, I'm Zainab</h1>
        <p>Chasing dreams & building web magic âœ¨</p>
        <button className="hire-button">Hire Me</button>
      </div>

      {/* Blob + Image */}
      <div className="blob-container">
        <div className="blob"></div>
        <img src="/assets/profile.png" alt="Zainab" className="profile-img" />
      </div>
    </section>
    
  );
}

export default Home;
