import React, { useEffect, useRef, useState } from "react";
import "../styles/Home.css";

function Home() {
  const introRef = useRef(null);
  const timelineRef = useRef(null);

  const [introVisible, setIntroVisible] = useState(false);
  const [timelineVisible, setTimelineVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.target === introRef.current && entry.isIntersecting) {
            setIntroVisible(true);
          }
          if (entry.target === timelineRef.current && entry.isIntersecting) {
            setTimelineVisible(true);
          }
        });
      },
      { threshold: 0.2 }
    );

    if (introRef.current) observer.observe(introRef.current);
    if (timelineRef.current) observer.observe(timelineRef.current);

    return () => {
      if (introRef.current) observer.unobserve(introRef.current);
      if (timelineRef.current) observer.unobserve(timelineRef.current);
    };
  }, []);

  return (
    <div className="home">
      <div
        className={`hero-section ${introVisible ? "visible" : ""}`}
        ref={introRef}
      >
        <div className="text-side">
          <h1 className="intro-title">Hi, I'm Zainab</h1>
          <p>
            A passionate frontend developer, currently studying O-Levels,
            balancing both worlds with dedication.
          </p>
          <button className="hire-button">Hire Me</button>
        </div>
        <div className="blob-container">
          <div className="blob"></div>
          <img
            src="assets/profile.png"
            alt="Zainab"
            className="profile-img"
          />
        </div>
      </div>

      <div className="story-timeline">
        <h2 className="timeline-heading">My Journey as a Dream Chaser</h2>
        <div
          ref={timelineRef}
          className={`timeline ${timelineVisible ? "visible" : ""}`}
        >
          {[...Array(6)].map((_, i) => (
            <div key={i} className="chapter">
              <div className="circle">{i + 1}</div>
              <div className="line"></div>
              <div className="chapter-box">
                <h3>Chapter {i + 1}</h3>
                <p className="description">
                  {[
                    "Discovering my interest in design and code.",
                    "Learning HTML, CSS, and JavaScript step by step.",
                    "Diving into React and creating cool projects.",
                    "Working with real clients & internships.",
                    "Balancing studies with my passion for web dev.",
                    "Now building dreamy portfolios & chasing bigger goals.",
                  ][i]}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
