import React, { useEffect, useRef, useState } from "react";
import "../styles/Home.css";

function Home() {
  const timelineRef = useRef(null);
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) {
            setIsVisible(true);
            observer.unobserve(entry.target); // Remove once visible
          }
        });
      },
      { threshold: 0.2 }
    );

    if (timelineRef.current) {
      observer.observe(timelineRef.current);
    }

    return () => {
      if (timelineRef.current) {
        observer.unobserve(timelineRef.current);
      }
    };
  }, []);

  return (
    <div className="home">
      {/* ... hero section ... */}

      <div className="story-timeline">
        <h2 className="timeline-heading">My Journey as a Dream Chaser</h2>
        <div
          ref={timelineRef}
          className={`timeline ${isVisible ? "visible" : ""}`}
        >
          {[...Array(6)].map((_, i) => (
            <div key={i} className="chapter">
              <div className="circle">{i + 1}</div>
              <div className="line"></div>
              <div className="chapter-box">
                <h3>Chapter {i + 1}</h3>
                <p className="description">
                  {[
                    "Discovering my interest in design and code.",
                    "Learning HTML, CSS, and JavaScript step by step.",
                    "Diving into React and creating cool projects.",
                    "Working with real clients & internships.",
                    "Balancing studies with my passion for web dev.",
                    "Now building dreamy portfolios & chasing bigger goals.",
                  ][i]}
                </p>
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Home;
